import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Briefcase } from 'lucide-react';

const Projects: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  // Real SQA projects/roles as projects
  const projects = [
    {
      title: "Senior SQA Engineer, Transax",
      period: "Jan 2023 - Present",
      responsibilities: [
        "Managed the QA procedures for the Transax project (an online platform allowing customers to start their purchase from dealers' websites), to guarantee that software releases adhered to quality standards through rigorous regression and sanity testing.",
        "Trusted to independently manage the full QA lifecycle for large-scale SaaS releases — from test planning to execution, defect triage, and stakeholder reporting.",
        "Implemented automated testing using Cypress, resulting in a reduction in testing time and an increase in test coverage.",
        "Performed end-to-end testing of web apps built with Vue.js frontend and Laravel PHP backend, ensuring smooth functionality across customer journeys and data layers.",
        "Tested real-world user flows including onboarding, bookings, automated messages, and integrations to ensure a seamless guest experience.",
        "Addressed inquiries from developers, product owners, and stakeholders, delivering valuable feedback on new features. Authored and sustained test cases, defined acceptance criteria, and established quality metrics.",
        "Collaborated closely with developers to troubleshoot and resolve bugs, ensuring the timely delivery of high-quality software.",
        "Aiming to ensure client satisfaction and foster lasting relationships."
      ]
    },
    {
      title: "SQA Engineer, Tech Bazaar",
      period: "Feb 2022 – Dec 22",
      responsibilities: [
        "Participated in the initial stages of the project, actively contributing insights during requirement-gathering sessions.",
        "Collaborated with cross-functional teams to define project scope and establish comprehensive testing requirements.",
        "Created and maintained detailed test plans, test cases, and acceptance criteria, ensuring comprehensive coverage of functional and non-functional requirements.",
        "Executed rigorous testing protocols, including regression, integration, and performance testing, to identify and rectify issues promptly.",
        "Participated in regular status meetings, delivering progress updates and insights into the QA process.",
        "Led a team of 3 QA members, collaborating closely and providing valuable insights for the testing protocols."
      ]
    },
    {
      title: "QA Engineer, Eagle 6",
      period: "Nov 2020 – Jan 22",
      responsibilities: [
        "As a QA Engineer contributing to the Eagle6 cybersecurity initiative, I played a pivotal role in upholding Eagle6's mission of partnering with a proven expert in cyber model-driven visualization.",
        "Applied specialized regression testing techniques to identify and address potential security vulnerabilities.",
        "Authored precise Gherkin scripts, following a behavior-driven development (BDD) approach to define test scenarios.",
        "Implemented automation solutions by translating Gherkin scripts into executable test scripts using Selenium with the Cucumber framework in Java.",
        "Maintained meticulous documentation of test cases, scenarios, and results, ensuring transparency and facilitating comprehensive project reporting aligned with the mission's objectives."
      ]
    }
  ];

  return (
    <section id="projects" className="section-padding">
      <div className="container-custom">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Professional <span className="gradient-text">Projects</span>
          </h2>
          <p className="text-lg text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Real-world SQA projects and roles, highlighting key responsibilities and achievements in software quality assurance, automation, and process improvement.
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={project.title}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: index * 0.1 }}
              className="group"
            >
              <div className="bg-white dark:bg-dark-800 rounded-lg overflow-hidden shadow-lg hover:shadow-xl transition-all duration-300 card-hover h-full flex flex-col">
                <div className="p-6 flex-1 flex flex-col">
                  <div className="flex items-center gap-2 mb-3">
                    <Briefcase className="w-5 h-5 text-primary-600 dark:text-primary-400" />
                    <span className="text-sm text-primary-600 dark:text-primary-400 font-medium">
                      {project.period}
                    </span>
                  </div>
                  <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-3">
                    {project.title}
                  </h3>
                  <ul className="list-disc pl-5 text-gray-600 dark:text-gray-300 space-y-2">
                    {project.responsibilities.map((item, i) => (
                      <li key={i}>{item}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
