import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Skills: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const skillCategories = [
    {
      category: "Automation & Testing Tools",
      skills: [
        { name: "Cypress", level: 95 },
        { name: "Selenium", level: 92 },
        { name: "Cucumber", level: 90 },
        { name: "Postman", level: 90 },
        { name: "REST APIs", level: 88 },
        { name: "Jest", level: 80 }
      ]
    },
    {
      category: "Programming & Scripting",
      skills: [
        { name: "JavaScript", level: 90 },
        { name: "TypeScript", level: 85 },
        { name: "Java", level: 88 },
        { name: "Node.js", level: 80 },
        { name: "PHP (Laravel)", level: 75 }
      ]
    },
    {
      category: "Frameworks & Platforms",
      skills: [
        { name: "React", level: 85 },
        { name: "Vue.js", level: 80 },
        { name: "JIRA", level: 95 },
        { name: "Notion", level: 80 },
        { name: "Slack", level: 80 }
      ]
    },
    {
      category: "CI/CD & DevOps",
      skills: [
        { name: "GitHub Actions", level: 85 },
        { name: "Bitbucket Pipelines", level: 80 },
        { name: "Jenkins", level: 75 }
      ]
    },
    {
      category: "Databases",
      skills: [
        { name: "MySQL", level: 85 },
        { name: "Azure SQL", level: 80 }
      ]
    }
  ];

  const ProgressBar: React.FC<{ skill: { name: string; level: number } }> = ({ skill }) => {
    return (
      <div className="mb-4">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
            {skill.name}
          </span>
          <span className="text-sm text-gray-500 dark:text-gray-400">
            {skill.level}%
          </span>
        </div>
        <div className="w-full bg-gray-200 dark:bg-dark-700 rounded-full h-2">
          <motion.div
            className="bg-gradient-to-r from-primary-500 to-primary-600 h-2 rounded-full"
            initial={{ width: 0 }}
            animate={inView ? { width: `${skill.level}%` } : { width: 0 }}
            transition={{ duration: 1, delay: 0.2 }}
          />
        </div>
      </div>
    );
  };

  return (
    <section id="skills" className="section-padding">
      <div className="container-custom">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Technical <span className="gradient-text">Skills</span>
          </h2>
          <p className="text-lg text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            ISTQB® Certified with a strong foundation in software testing, automation, and quality management. Proficient in modern testing tools, frameworks, and Agile/Scrum methodologies.
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {skillCategories.map((category, categoryIndex) => (
            <motion.div
              key={category.category}
              initial={{ opacity: 0, y: 30 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: categoryIndex * 0.1 }}
              className={`p-6 rounded-lg border transition-all duration-300 hover:shadow-lg card-hover ${
                inView ? 'border-primary-200 bg-primary-50 dark:border-primary-700 dark:bg-dark-800' : 
                'border-gray-200 dark:border-dark-700'
              }`}
            >
              <h3 className="text-xl font-bold mb-6 text-gray-900 dark:text-white">
                {category.category}
              </h3>
              <div className="space-y-4">
                {category.skills.map((skill, skillIndex) => (
                  <ProgressBar key={skill.name} skill={skill} />
                ))}
              </div>
            </motion.div>
          ))}
        </div>

        {/* Additional Skills */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="mt-16"
        >
          <h3 className="text-2xl font-bold text-center mb-8 text-gray-900 dark:text-white">
            Additional Skills & Certifications
          </h3>
          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
            {[
              "ISTQB® - CTFL Certification (2023)",
              "Agile/Scrum Methodologies",
              "Cross-browser & Responsive Testing",
              "Team Leadership & Mentoring",
              "Test Planning & Documentation",
              "Security & Compliance Testing",
              "API & Mobile Testing",
              "Collaboration: JIRA, Notion, Slack"
            ].map((skill, index) => (
              <motion.div
                key={skill}
                initial={{ opacity: 0, scale: 0.9 }}
                animate={inView ? { opacity: 1, scale: 1 } : {}}
                transition={{ duration: 0.5, delay: 0.8 + index * 0.1 }}
                className="text-center p-4 rounded-lg bg-gray-100 dark:bg-dark-700 hover:bg-primary-100 dark:hover:bg-primary-900 transition-colors duration-300"
              >
                <span className="text-gray-700 dark:text-gray-300 font-medium">
                  {skill}
                </span>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Skills;
